import * as types from "../../actionTypes";
import FreelancerApi from "../../../api/FreelancerApi";

let actionsFunction = {

  //CRUD METHODS

  // Create freelancer
  createFreelancer: function(freelancer) {
    return function(dispatch) {
      return FreelancerApi
        .createFreelancer(freelancer)
        .then(freelancer => {
          dispatch(actionsFunction.createFreelancerSuccess(freelancer));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createFreelancerSuccess: function(freelancer) {
    return { type: types.CREATE_FREELANCER_SUCCESS, payload: freelancer };
  },


  // Delete freelancer
  deleteFreelancer: function(id) {
    return function(dispatch) {
      return FreelancerApi
        .deleteFreelancer(id)
        .then(freelancer => {
          dispatch(actionsFunction.deleteFreelancerSuccess(freelancer));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteFreelancerSuccess: function(freelancer) {
    return { type: types.DELETE_FREELANCER_SUCCESS, payload: freelancer };
  },


  // Get freelancer
  loadFreelancer: function(id) {
    return function(dispatch) {
      return FreelancerApi
        .getOneFreelancer(id)
        .then(freelancer => {
          dispatch(actionsFunction.loadFreelancerSuccess(freelancer));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadFreelancerSuccess: function(freelancer) {
    return { type: types.GET_FREELANCER_SUCCESS, payload: freelancer };
  },

  // Load  list
  loadFreelancerList: function() {
    return function(dispatch) {
      return FreelancerApi
        .getFreelancerList()
        .then(list => {
          dispatch(actionsFunction.loadFreelancerListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadFreelancerListSuccess: function(list) {
    return { type: types.LIST_FREELANCER_SUCCESS, payload: list };
  },

	
  // Save freelancer
  saveFreelancer: function(freelancer) {
    return function(dispatch) {
      return FreelancerApi
        .saveFreelancer(freelancer)
        .then(freelancer => {
          dispatch(actionsFunction.saveFreelancerSuccess(freelancer));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveFreelancerSuccess: function(freelancer) {
    return { type: types.UPDATE_FREELANCER_SUCCESS, payload: freelancer };
  },


};

export default actionsFunction;
