import * as types from "../../actionTypes";
import PartnerApi from "../../../api/PartnerApi";

let actionsFunction = {

  //CRUD METHODS

  // Create partner
  createPartner: function(partner) {
    return function(dispatch) {
      return PartnerApi
        .createPartner(partner)
        .then(partner => {
          dispatch(actionsFunction.createPartnerSuccess(partner));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createPartnerSuccess: function(partner) {
    return { type: types.CREATE_PARTNER_SUCCESS, payload: partner };
  },


  // Delete partner
  deletePartner: function(id) {
    return function(dispatch) {
      return PartnerApi
        .deletePartner(id)
        .then(partner => {
          dispatch(actionsFunction.deletePartnerSuccess(partner));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deletePartnerSuccess: function(partner) {
    return { type: types.DELETE_PARTNER_SUCCESS, payload: partner };
  },


  // Get partner
  loadPartner: function(id) {
    return function(dispatch) {
      return PartnerApi
        .getOnePartner(id)
        .then(partner => {
          dispatch(actionsFunction.loadPartnerSuccess(partner));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadPartnerSuccess: function(partner) {
    return { type: types.GET_PARTNER_SUCCESS, payload: partner };
  },

  // Load  list
  loadPartnerList: function() {
    return function(dispatch) {
      return PartnerApi
        .getPartnerList()
        .then(list => {
          dispatch(actionsFunction.loadPartnerListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadPartnerListSuccess: function(list) {
    return { type: types.LIST_PARTNER_SUCCESS, payload: list };
  },

	
  // Save partner
  savePartner: function(partner) {
    return function(dispatch) {
      return PartnerApi
        .savePartner(partner)
        .then(partner => {
          dispatch(actionsFunction.savePartnerSuccess(partner));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  savePartnerSuccess: function(partner) {
    return { type: types.UPDATE_PARTNER_SUCCESS, payload: partner };
  },


};

export default actionsFunction;
